mapper.select:
SELECT e.id, e.element_name, e.description, e.id_equipment, e.type, e.estado
FROM element as e
WHERE e.id = #{id};

mapper.update:
UPDATE element
SET id_equipment = #{idEquipment}
WHERE id = #{idElement};

UPDATE element
SET estado = #{estado}
WHERE id = #{id};

mapper.select:
SELECT e.id, e.element_name, e.description, e.id_equipment, e.type, e.estado
FROM element as e
WHERE e.id_equipment = #{idEquipment} AND e.type = #{tipo};
